class PalindromicSubstringsDiv2 {
public:
    string s;
    int n;
    bool palin[5005][5005];
    bool ispalin(int l, int r)
    {
        if(l >= r)
            return 1;
        bool &ret = palin[l][r];
        if(ret != 0)
            return ret;
        if(s[l] == s[r])
            return ret = ispalin(l + 1, r - 1);
        return 0;
    }
	int count(vector <string> S1, vector <string> S2) {
		int i, res, j;
		clr(palin, 0);
		n = SZ(S1);
		for(i = 0; i < n; i++)
            s += S1[i];
        n = SZ(S2);
		for(i = 0; i < n; i++)
            s += S2[i];
        n = SZ(s);
        for(i = 0; i < n; i++)
            for(j = i; j < n; j++)
                res += ispalin(i, j);
        return res;
	}
};


